defmodule Solution do
  def run(input) do
    input |> String.strip |> count |> IO.puts
  end

  defp count("(" <> rest) do
    1 + count(rest)
  end

  defp count(")" <> rest) do
    -1 + count(rest)
  end

  defp count("") do
    0
  end
end

